import {BrowserRouter, Route, Routes, Navigate} from 'react-router-dom';
import { useAuthContext } from './hooks/useAuthContext';

import Navbar from './components/Navbar';
import MainPort from './screens/MainPort';
import SignupLoginPage from './screens/SignupLoginPage';
import UserPage from './screens/UserPage';
import SearchPage from './screens/SearchPage';

function App() {
  const {user} = useAuthContext();

  return (
    <div>
      <BrowserRouter>
      <Navbar/>
      <div className="pages">
        <Routes>
          <Route path="/" exact element={<MainPort/>}/>
          <Route path="/search" element={<SearchPage/>}/>
          <Route path="/login" element={user ? <Navigate to="/user"/> : <SignupLoginPage/>}/>
          <Route path="/user" element={!user ? <Navigate to ="/login"/> : <UserPage/>}/>
        </Routes>
      </div>
      </BrowserRouter>
    </div>
  )
}
export default App;
