var chai = require('chai');
var expect = require('chai').expect;
var encrypt = require('../index.js');

describe('test_encryption',function(){
	it('encrypt_any_string',function(){
		var word1 = encrypt.encr("its a hot day");
		var word2 = encrypt.encr("its a hot day");
		expect(word1).to.equal(word2)
	})
})